    public void GetUsers()
    {
        // clear any error messages
        strError = "";
        // Collection to hold users
        ColUsers = new List<IdentityUser>
            ();
        ColRoles = new List<IdentityRole>
            ();
        // get users from __UserManager
        var user = _UserManager.Users.Select(x => new IdentityUser
        {
            Id = x.Id,
            UserName = x.UserName,
            Email = x.Email,
            PasswordHash = "*****",
            EmailConfirmed=x.EmailConfirmed
        });

        foreach (var item in user)
        {
            ColUsers.Add(item);
        }

    }


    async Task EditHandler(GridCommandEventArgs args)
    {
        IdentityUser item = (IdentityUser)args.Item;
        // Get the user
        var user = await _UserManager.FindByIdAsync(item.Id);
        if (user != null)
        {
            // Is user in administrator role?
            var UserResult =
            await _UserManager
            .IsInRoleAsync(user, ADMINISTRATION_ROLE);
            if (UserResult)
            {
                CurrentUserRole = ADMINISTRATION_ROLE;

            }
            else
            {
                CurrentUserRole = "Users";

            }

            CurrentUserConfirme = user.EmailConfirmed;
        }
    }

    async Task UpdateHandler(GridCommandEventArgs args)
    {
        IdentityUser item = (IdentityUser)args.Item;
        // Get the user

        var user = await _UserManager.FindByIdAsync(item.Id);
        // Update Email

        user.Email = item.Email;
        // Update the user

        item.EmailConfirmed = CurrentUserConfirme;
        user.EmailConfirmed = item.EmailConfirmed;


        await _UserManager.UpdateAsync(user);
        // Only update password if the current value
        // is not the default value
        if (item.PasswordHash != "*****")
        {
            var resetToken =
            await _UserManager.GeneratePasswordResetTokenAsync(user);
            var passworduser =
            await _UserManager.ResetPasswordAsync(
            user,
            resetToken,
            item.PasswordHash);
            if (!passworduser.Succeeded)
            {
                if (passworduser.Errors.FirstOrDefault() != null)
                {
                    strError =
                    passworduser
                    .Errors
                    .FirstOrDefault()
                    .Description;
                }
                else
                {
                    strError = "Pasword error";
                }
            }

        }
        // Handle Roles
        // Is user in administrator role?
        var UserResult =
        await _UserManager
        .IsInRoleAsync(user, ADMINISTRATION_ROLE);
        // Is Administrator role selected
        // but user is not an Administrator?
        if (
        (CurrentUserRole == ADMINISTRATION_ROLE)
        &
        (!UserResult))
        {
            // Put admin in Administrator role
            await _UserManager
            .AddToRoleAsync(user, ADMINISTRATION_ROLE);
        }
        else
        {
            // Is Administrator role not selected
            // but user is an Administrator?
            if ((CurrentUserRole != ADMINISTRATION_ROLE)
            &
            (UserResult))
            {
                // Remove user from Administrator role
                await _UserManager
                .RemoveFromRoleAsync(user, ADMINISTRATION_ROLE);
            }
        }

    }

    async Task CreateHandler(GridCommandEventArgs args)
    {
        IdentityUser item = (IdentityUser)args.Item;
        // Make new user
        objUser = new IdentityUser();
        objUser.PasswordHash = "*****";
        // Set Id to blank so we know it is a new record
        objUser.Id = "";
        objUser.EmailConfirmed = false;
        // Open the Popup
        await OnInitializedAsync();

    }

    public async Task DeleteHandler(GridCommandEventArgs args)

    {
        await ShowWindow();

        await Task.Delay(3000);

        StateHasChanged();

        await Task.Delay(1000);

        StateHasChanged();

        if (DeleteConfirm)
        {
            IdentityUser item = (IdentityUser)args.Item;
            // Get the user
            var user = await _UserManager.FindByIdAsync(item.Id);
            if (user != null)
            {
                // Delete the user
                await _UserManager.DeleteAsync(user);
            }
            // Refresh Users
        }

        DeleteConfirm = false;
        StateHasChanged();
        await OnInitializedAsync();

    }

    public async Task ShowWindow()
    {
        isVisible = true;
        await OnInitializedAsync();
        StateHasChanged();

    }

    public async Task Confirm()
    {
        DeleteConfirm = true;
        isVisible = false;
        await OnInitializedAsync();
        StateHasChanged();
        return;
    }

    public async Task CloseWindow()
    {
        DeleteConfirm = false;
        isVisible = false;
        await OnInitializedAsync();
        StateHasChanged();
        return;
    }
    async Task CancelHandler(GridCommandEventArgs args)
    {
        await Task.Delay(500); //simulate actual long running async operation
    }



